update.if:
update cs_admin_menu set
    ref = ${node_parent},
    ref_step = (select ref_step+1 from cs_admin_menu where idx = ${node_parent}),
    ref_level = ${node_position}
where idx = ${node}

if.foreach:
update cs_admin_menu set
ref_level = ${index}
where idx = ${item}

mapper.update:
update cs_admin_menu set
    name = #{name},
    link = #{link},
    state = #{state}
where idx = #{idx}

update cs_admin_menu set
    del_flag = 1
where idx = #{idx}
